if (window.isFrameInDifferentDomain || window.isInIframe) {
 var scroll = event.value;//presenter.playerController.iframeScroll();
} else {
 var scroll = event.value;
}
var scrollModule = presenter.playerController.getFooterModule('__scroll_top');
if (scrollModule) {
 var scrollTopView = scrollModule.getView();
 if (scroll > 0) {
 $(scrollTopView).removeClass('disable');
 } else if (!$(scrollTopView).hasClass('disable')){
 $(scrollTopView).addClass('disable');
 }
}

