Name:PageLoaded
SCRIPTSTART
if (window.isFrameInDifferentDomain || window.isInIframe) {
 var scroll = presenter.playerController.iframeScroll();
} else {
 var scroll = 0;
 $(".ic_header").parents().each(function(){
 scroll = Math.max(scroll, $(this).scrollTop());
 });
}

var scrollModule = presenter.playerController.getFooterModule('__scroll_top');
var resetModule = presenter.playerController.getFooterModule('__reset');
var checkModule = presenter.playerController.getFooterModule('__check');
var showAnswersModule = presenter.playerController.getFooterModule('__show_answers');
var openReportModule = presenter.playerController.getFooterModule('__open_report');
var disableModule = presenter.playerController.getFooterModule('__disable_area');
var disableCheckModule = presenter.playerController.getFooterModule('__disable_check');
var disableResetModule = presenter.playerController.getFooterModule('__disable_reset');
var disableShowAnswersModule = presenter.playerController.getFooterModule('__disable_show_answers');

if (scrollModule) {
 var scrollTopView = scrollModule.getView();
 $(scrollTopView).off('click').on('click', function(e) {
 if (window.isFrameInDifferentDomain || window.isInIframe) {
 $('html, body, #content-view', window.parent.document).animate({scrollTop:0}, 'slow');
 } else {
 $('html, body').animate({scrollTop:0}, 'slow');
 }
 });
 if (scroll > 0) {
 $(scrollTopView).removeClass('disable');
 } else if (!$(scrollTopView).hasClass('disable')){
 $(scrollTopView).addClass('disable');
 }
}

var pageCount = presenter.playerController.getPresentation().getPageCount();

var currentPageIndex = presenter.playerController.getCurrentPageIndex();

var page = presenter.playerController.getPresentation().getPage(currentPageIndex);

if (!page.isReportable()) {
 if (checkModule) {
 checkModule.hide();
 }
 if (disableCheckModule) {
 disableCheckModule.show();
 }
 if (resetModule) {
 resetModule.hide();
 }
 if (disableResetModule) {
 disableResetModule.show();
 }
 if (showAnswersModule) {
 showAnswersModule.hide();
 }
 if (disableShowAnswersModule) {
 disableShowAnswersModule.show();
 }
} else {
 if (checkModule) {
 checkModule.show();
 }
 if (disableCheckModule) {
 disableCheckModule.hide();
 }
 if (resetModule) {
 resetModule.hide();
 }
 if (disableResetModule) {
 disableResetModule.show();
 }
 if (showAnswersModule) {
 showAnswersModule.hide();
 }
 if (disableShowAnswersModule) {
 disableShowAnswersModule.show();
 }
}


SCRIPTEND
