var disableResetModule = presenter.playerController.getFooterModule('__disable_reset');
var disableShowAnswersModule = presenter.playerController.getFooterModule('__disable_show_answers');

if (!page.isReportable()) {
 if (checkModule) {
 checkModule.hide();
 }
 if (disableCheckModule) {
 disableCheckModule.show();
 }
 if (resetModule) {
 resetModule.hide();
 }
 if (disableResetModule) {
 disableResetModule.show();
 }
 if (showAnswersModule) {
 showAnswersModule.hide();
 }
 if (disableShowAnswersModule) {
 disableShowAnswersModule.show();
 }
} else {
 if (checkModule) {
 checkModule.show();
 }
 if (disableCheckModule) {
 disableCheckModule.hide();
 }
 if (resetModule) {
 resetModule.hide();
 }
 if (disableResetModule) {
 disableResetModule.show();
 }
 if (showAnswersModule) {
 showAnswersModule.hide();
 }
 if (disableShowAnswersModule) {
 disableShowAnswersModule.show();
 }
}

var feedbackModule = presenter.playerController.getFooterModule('__open_feedback');
if (feedbackModule) {
 feedbackModule.disable();
}

