SCRIPTSTART

 var text = presenter.playerController.getModule('PuntuacionB');

 if (PuntuacionB.textContent === '0'){ 
 presenter.playerController.getModule('SelB1').deselect();
 presenter.playerController.getModule('SelB2').deselect();
 presenter.playerController.getModule('SelB3').deselect();
 presenter.playerController.getModule('SelB4').deselect();

 }

 if (PuntuacionB.textContent === '1'){ 
 presenter.playerController.getModule('SelB1').select();
 presenter.playerController.getModule('SelB2').deselect();
 presenter.playerController.getModule('SelB3').deselect();
 presenter.playerController.getModule('SelB4').deselect();

 }

 if (PuntuacionB.textContent === '2'){ 
 presenter.playerController.getModule('SelB1').select();
 presenter.playerController.getModule('SelB2').select();
 presenter.playerController.getModule('SelB3').deselect();
 presenter.playerController.getModule('SelB4').deselect();

 }

 if (PuntuacionB.textContent === '3'){ 
 presenter.playerController.getModule('SelB1').select();
 presenter.playerController.getModule('SelB2').select();
 presenter.playerController.getModule('SelB3').select();
 presenter.playerController.getModule('SelB4').deselect();

 }

 if (PuntuacionB.textContent === '4'){ 
 presenter.playerController.getModule('SelB1').select();
 presenter.playerController.getModule('SelB2').select();
 presenter.playerController.getModule('SelB3').select();
 presenter.playerController.getModule('SelB4').select();

 }


SCRIPTEND
